import React, { Component } from 'react'
import { Route } from 'react-router-dom';
import './SASS/Main.scss'
import Characters from './Characters'



export default class App extends Component {
  constructor() {
    super()
    this.state = {
      favorites: [],
      chatAbout: '',
      inputMessage: '',
      allMessages: []
    }
  }

  favoritesHandler = (favorite) => {
    let fav = [...this.state.favorites, favorite]
    let existsIndex = this.state.favorites.indexOf(favorite);
    if (existsIndex === -1) {
      this.setState({ favorites: fav })
    } else {
      let without = [...this.state.favorites]
      without.splice(existsIndex, 1)
      this.setState({ favorites: without })
    }
  }

  chatRouteHandler = (character) => {
    console.log(character)
    this.setState({ chatAbout: character })
  }

  txtChangedHandler = (event) => {
    const inputValue = event.target.value;
    this.setState((state, prevState) => {
      return { inputMesssage: inputValue }
    });
  }

  messageSubmit = (state, prevState) => {
    this.setState((state, prevState) => {
      return { allMessages: [...this.prevState.allMessages, this.prevState.inputMessage] };
    })
  }

  render() {
    return (
      <div>
        <Route
          path="/"
          exact
          render={() => (
            <div>
              <div className="logo">
                <img
                  className="logo_image"
                  src="https://proxy.duckduckgo.com/iu/?u=https%3A%2F%2Fupload.wikimedia.org%2Fwikipedia%2Fcommons%2Fthumb%2F6%2F6c%2FStar_Wars_Logo.svg%2F1200px-Star_Wars_Logo.svg.png&f=1"
                  alt="star wars logo"
                />
              </div>
              <p>Vote on your favorite characters. This was built using the <a target="blank" href="https://www.swapi.co/">star wars api</a>.</p>
              {this.state.favorites.length > 0
                ? (<p>Your favorite characters are {this.state.favorites.map((favorite) => {
                  if (this.state.favorites.length > 1) {
                    if (favorite === this.state.favorites[this.state.favorites.length - 1]) {
                      return favorite
                    } else {
                      return favorite + ", "
                    }
                  } else {
                    return favorite
                  }
                })}</p>)
                : null}
              <Characters chatHandler={this.chatRouteHandler} number="1" favorites={this.favoritesHandler} />
            </div>
          )}
        />
        <Route
          path="/discuss"
          exact
          render={() => (
            <div className="discussion">
              <h1>Discuss {this.state.chatAbout}</h1>
              <div className="discussion_chatbox">
                {this.state.allMessages < 1 ?
                  <p>It appears that there are no messages yet. Post the first comment below!</p>
                  : (
                    this.state.allMessages.map((message, i) => {
                      return (
                        <p key={i} className="message">{message}</p>
                      )
                    })
                  )
                }


                <div className="discussion_chatbox_bar">
                  <input onChange={this.txtChangedHandler} type="text" placeholder="send message" />
                  <button onClick={this.messageSubmit}>&#8594;</button>
                </div>

              </div>
            </div>
          )}
        />
      </div>
    )
  }
}

