import React, { Component } from 'react'
import './SASS/Main.scss'
import Characters from './Characters'


export default class App extends Component {
  constructor() {
    super()
    this.state = {
      favorites: []
    }
  }

  favoritesHandler = favorite => {
    const fav = [...this.state.favorites, favorite]
    this.setState({
      favorites: [...this.state.favorites, favorite]
    })
    console.log(this.state.favorites)
  }

  render() {
    return (
      <div>
        <div className="logo">
          <img
            className="logo_image"
            src="https://proxy.duckduckgo.com/iu/?u=https%3A%2F%2Fupload.wikimedia.org%2Fwikipedia%2Fcommons%2Fthumb%2F6%2F6c%2FStar_Wars_Logo.svg%2F1200px-Star_Wars_Logo.svg.png&f=1"
            alt="star wars logo"
          />
        </div>
        <p>Vote on your favorite characters. This was built using the <a href="https://www.swapi.co/">star wars api</a>.</p>
        {this.state.favorites.length > 0
          ? (<p>Your favorite characters are {this.state.favorites.forEach((favorite) => {
            return favorite + ","
          })}</p>) : null}
        <Characters favorites={this.favoritesHandler} />
      </div>
    )
  }
}

